import Foundation

struct MarketPlaceListing: Identifiable {
	var id: UUID
	var ownerID: UUID
	var title: String
	var description: String
	var location: (Double, Double)
	var category: String
	var itemDetails: [String]
	var images: [String] //Assumed to be a list of image URLs
	var price: Int
	
	static var mockData = MarketPlaceListing(id: UUID(), ownerID: UUID(), title: "Car", description: "This is a car. It's very good", location: (37.3387, -121.8853), category: "Cars and Trucks", itemDetails: ["Miles 100K"], images: ["data:image/jpeg;base64,",
																																																																														 "https://www.kimballstock.com/images/car-stock-photos/cutout-car-images.jpg"], price: 12000)
    static var mockDataList:[MarketPlaceListing] = [ thumbToListing(thumb: marketPlaceMockDataThumbnailArr[0], category: "Technology", description: "Electronics very good"), thumbToListing(thumb: marketPlaceMockDataThumbnailArr[1], category: "Shopping", description: "Very Cool Clothes") ]
    
}

func thumbToListing(thumb: MarketPlaceThumbnail, category: String, description: String) -> MarketPlaceListing {
	return MarketPlaceListing(id: thumb.listingID, ownerID: thumb.listingID, title: thumb.title, description: description, location: thumb.location, category: category, itemDetails: [thumb.displayedDetail], images: [thumb.image], price: thumb.price)
}


struct MarketPlaceThumbnail {
	var listingID: UUID
